#!/usr/bin/env bash [[ -f "${TM_SUPPORT_PATH}/lib/bash_init.sh" ]] && . "${TM_SUPPORT_PATH}/lib/bash_init.sh"
# TM_IGNORE_WARNINGS is a sequence of glob patterns of files in which to ignore # warnings. Right now it doesn't support escaped ':" -- the separator character. # export TM_IGNORE_WARNINGS="mitchcode_*.h:*/MitchCode/*"
# Look at other bundles to see if there are conventions for using 'defaults'. PREF_DOMAIN=com.macromates.textmate.scons_prefs lastTarget=`defaults read ${PREF_DOMAIN} last_target 2>/dev/null` lastBuildDir=`defaults read ${PREF_DOMAIN} last_build_dir 2>/dev/null`
if [ "x$lastBuildDir" != "x" ]; then cd ${lastBuildDir} fi
# Allow spaces to delimit arguments in ${target} -- bad idea? python -u "${TM_BUNDLE_SUPPORT}/bin/scons_html_wrapper.py" ${lastTarget}
